        if(arr[i]&amp;gt;maxData)
        &amp;#123;
            maxData=arr[i];
        &amp;#125;
    &amp;#125;

    int d=1;int p=10;
    while(maxData&amp;gt;=p)
    &amp;#123;
         maxData /= 10;
        ++d;
    &amp;#125;
    return d;
&amp;#125;

template &amp;lt;typename T&amp;gt;
void radix_sortT *arr, int n)
&amp;#123;
    int d=maxbit(arr,n);
    int *tmp=new int[n];
    int *count=new int[10];
    int i,j,k;
    int radix=1;

    for(i=1;i&amp;lt;=d;i++)
    &amp;#123;
        for(j=0;j&amp;lt;10;j++)
        &amp;#123;
            count[j]=0;
        &amp;#125;
        for(j=0;j&amp;lt;n;j++)
        &amp;#123;
            k=(arr[j]/radix)%10;
            count[k]++;
        &amp;#125;
        for(j=1;j&amp;lt;10;j++)
        &amp;#123;
            count[j]=count[j-1]+count[j];
        &amp;#125;

        for(j=n-1;j&amp;gt;=0;j--)
        &amp;#123;
             k=(arr[j]/radix)%10;
            tmp[count[k]-1]=arr[j];
            count[k]--;
        &amp;#125;

        for(j=0;j&amp;lt;n;j++)
        &amp;#123;
            arr[j]=tmp[j];
        &amp;#125;
        radix=radix*10;
    &amp;#125;
    delete []tmp;
    delete []count;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
